<script>
function isOverRange(target, min, max) {
  if (target >= max || target <= min) {
    return true
  }
  return false
}
class Snow {
  constructor(innerWidth, innerHeight) {
    this.innerWidth = innerWidth
    this.innerHeight = innerHeight
    this.vr = 0.1 // 半径缩小的速度
    this.init()
  }
  init() {
    this.x = Math.floor(Math.random() * (this.innerWidth - 6) + 5)
    this.y = 1
    this.radius = Math.random() * 15 + 2
    const spead = this.radius * 0.1
    this.vx = Math.random() * spead - 0.5
    this.vy = this.radius * 0.15
  }
  draw(context) {
    if (this.radius < 1) {
      return
    }
    var gradient = context.createRadialGradient(
      this.x,
      this.y,
      this.radius / 2,
      this.x,
      this.y,
      this.radius
    )
    gradient.addColorStop(0, 'rgba(255,255,255,1)')
    gradient.addColorStop(1, 'rgba(255,255,255,0.1)')
    context.fillStyle = gradient
    context.beginPath()
    context.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false)
    context.closePath()
    context.fill()
  }
  move() {
    if (!this.canShowSnow && this.radius < 1) {
      return
    }
    this.x = this.x + this.vx
    this.y = this.y + this.vy
    if (
      isOverRange(this.x, 1, this.innerWidth) ||
      isOverRange(this.y, 1, this.innerHeight)
    ) {
      this.init()
    }
  }
  slowHide() {
    this.radius -= this.vr
  }
}
export default {
  name: 'SnowFalling',
  props: {
    canShowSnow: Boolean
  },
  data() {
    return {
      snows: [],
      count: 150
    }
  },
  methods: {
    animation(canvas, context) {
      context.clearRect(0, 0, canvas.width, canvas.height)
      let count = 0
      this.snows.forEach(snow => snow.draw(context))
      this.snows.forEach(snow => {
        snow.move()
        if (!this.canShowSnow) {
          snow.slowHide()
        }
        if (snow.radius < 1) {
          count++
        }
      })
      if (count < this.snows.length) {
        requestAnimationFrame(() => this.animation(canvas, context))
      }
    }
  },
  watch: {
    canShowSnow: function(newValue, oldValue) {
      if (newValue) {
        const { canvas } = this.$refs
        if (canvas) {
          canvas.width = window.innerWidth
          canvas.height = window.innerHeight
          const context = canvas.getContext('2d')
          this.snows = []
          for (let i = 0; i < this.count; i++) {
            const snow = new Snow(canvas.width, canvas.height)
            this.snows.push(snow)
          }
          this.animation(canvas, context)
        }
      }
    }
  }
}
</script>

